import React from 'react'
import AboutCard1 from '../Layout/AboutCard1';
import "./About.css";

const About = () => {

  const aboutUsData = [{
    img: "https://t3.ftcdn.net/jpg/02/94/62/14/360_F_294621430_9dwIpCeY1LqefWCcU23pP9i11BgzOS0N.jpg",
    name: "Sitka Home Art Gallery",
    designation: "Sitka Home Art Gallery",
    quotes: "Sitka Home Art Gallery showcases the work of artist/owner Sitka in a beautiful space curated by his wife, Heloise. As a self-describe, contemporary impressionist, Sitka infuses his works with brillliant colors that reflect light, energy, and a light-hearted vibe, drawing upon the beauty of Boston, New England, and New York State for inspiration. "
  },
  {
    img: "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcQvh4ppLQ-Gz0c0gvPAVpWQwvQDsv5ejj69Hw&usqp=CAU",
    name: "Galerie d'Orsay",
    designation: "Chief Technology Officer(CTO) @CareerPath",
    quotes: "Galerie d'Orsay offers works ranging from 16th century Old Masters such as Rembrandt and Renoir to early 20th century modernists such as Picasso and Miro to 21st century contemporary painters, print makers, and sculptors such as Bruno Zupan, Luc Leestemaker, Damien Hirst, and urban hip-hop artist Sen-1. "
  },
  {
    img: "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcS65fmiDPZhcExH_fk2QS40vYGDZZi9Qhso6g&usqp=CAU",
    name: "Gallery NAGA",
    designation: "Gallery NAGA",
    quotes: "NAGA specializes in contemporary art and studio furniture, typically unabashedly avant-garde and experimental.  Out of all the Newbury Street art galleries, Boston's NAGA is where you'll typically see the most cutting-edge pieces. "
  }];

  return (
    <div className="component">
      <h3>About Us</h3>
      <p>
      Career Path is the #1 job site in the world1 with over 250M unique visitors2 every month. Indeed strives to put job seekers first, giving them free access to search for jobs, post resumes, and research companies. Every day, we connect millions of people to new opportunities.

      Please note that Indeed and its affiliates are directly or indirectly owned by a publicly traded Japanese parent company, Recruit Holdings Co., Ltd.
      </p>
      <p>People</p>
      <AboutCard1 data={aboutUsData}/>
    </div>
  )
}

export default About;
